
public class Cilindro  extends Figura{
	private int raggio;
	private int altezza;
	//definisco il costruttore di default
	Cilindro(){
		this.raggio=0;
		this.altezza=0;
	}
	//definisco il costruttore parametrico
	Cilindro(int r, int a){
		this.raggio=r;
		this.altezza=a;
	}
	
	
	@Override
	public double CalcolaArea() {
		// TODO Auto-generated method stub
		return this.raggio*this.raggio*Math.PI;
	}

}
